DROP TABLE ##ALUNOS_SEM_PAI_LISTA_ESCOLAS
select 
YEAR(D.DT_MATR) AS AN_MATR,
B.*, 
A.CD_ALUNO,
NM_ALUNO,
CONVERT(VARCHAR,DT_NASCIMENTO_ALUNO,103) AS DT_NASC,
SG_UF_NASCIMENTO_ALUNO AS UF_NASC,
CD_SEXO_ALUNO AS SEXO,
DC_RACA_COR,
NM_PAIS_MEC AS PAÍS_ORIGEM,
NM_MAE_ALUNO AS MÃE_ALUNO,
C.RESPONSÁVEL,
C.ENDEREÇO,
C.NR,
C.COMPL,
C.BAIRRO,
IDADE
INTO ##ALUNOS_SEM_PAI_LISTA_ESCOLAS
from ALUNOS_SEM_INFORMACAO_PAI A
JOIN LISTA$ B ON A.CD_ESCOLA=B.CD_ESCOLA COLLATE SQL_Latin1_General_CP1_CI_AI
LEFT JOIN TBL_ENDERECOS_ALUNOS C ON A.cd_aluno=C.COD_ALUNO
